WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/39.0/connection.js")} 
var internshipApp = new sforce.SObject("Internship_Application__c"); 
internshipApp.Id = "{!Internship_Application__c.Id}"; 
internshipApp.Status__c = "Rejected"; 

var result = sforce.connection.update([internshipApp]); 
if (result[0].getBoolean("success")) { 
    window.location.reload(); // Refresh the page to reflect the updated status 
} else { 
    alert("Error updating the application status: " + result[0].errors.message); 
}
